	cout << endl;
	for (size_t i = 0; i < str.size(); i++){
		cout << ite[i];
	}
	cout << endl;
	ite = str.begin();
	for (; ite != str.end(); ite++){
		cout << *ite;
	}

	cout << endl;

	str.append(10, 'a');
	//cout << ite[16] << endl; 迭代器失效

	system("pause");
	return 0;
}
